export interface Input {
    type: InputTypes,
    theme: 'primary',
    customClassList: Array<string>,
    id: string,
    hasLabel: boolean,
    labelText: string,
    placeholder: string
}

export type InputTypes =
    | 'button'
    | 'checkbox'
    | 'color'
    | 'date'
    | 'datetime-local'
    | 'email'
    | 'file'
    | 'hidden'
    | 'image'
    | 'month'
    | 'number'
    | 'password'
    | 'radio'
    | 'range'
    | 'reset'
    | 'search'
    | 'submit'
    | 'tel'
    | 'text'
    | 'time'
    | 'url'
    | 'week';
